import React, {Switch} from "react";
import "./App.css";
import Home from "./pages/Home";
import MenuPage from "./pages/MenuPage";
import Locations from "./pages/Locations";
import Subscriptions from "./pages/Subcriptions";
import { BrowserRouter as Router, Route, Link, useParams } from 'react-router-dom';
import { BrowserRouter } from "react-router-dom";
import Layout from "./pages/Layout";
import NoPage from "./pages/NoPage";


const Favorites = () => (
  <div>
    <h2>Favorites</h2>
    {/* Display user's favorite items here */}
  </div>
);

const MenuItemDetails = () => {
  const menuItems = [
    { id: 1, name: 'Burger', price: 9.99 },
    { id: 2, name: 'Pizza', price: 12.99 },
    { id: 3, name: 'Salad', price: 7.99 },
    // Add more menu items as needed
  ];

  const { itemId } = useParams();
  // Fetch item details based on itemId
  const selectedItem = menuItems.find((item) => item.id === parseInt(itemId, 10));

  if (!selectedItem) {
    return <div>Item not found</div>;
  }

  return (
    <div>
      <h2>{selectedItem.name}</h2>
      <p>Price: ${selectedItem.price}</p>
      <img src={selectedItem.image} alt={selectedItem.name} />
      {/* Add logic for favoriting the item */}
    </div>
  );
};

const App = () => (
    <BrowserRouter>
    <Routes>
      <Route path="/" element={<Layout />}>
        <Route index element={<Home />} />
        <Route path="MenuPage" element={<MenuPage />} />
        <Route path="Locations" element={<Locations />} />
        <Route path="Subscriptions" element={<Subscriptions/>}/>
        <Route path="*" element={<NoPage />} />
      </Route>
    </Routes>
  </BrowserRouter>
  );


