const Menu = () => {
// Wrap the entire logic in a DOMContentLoaded event listener
document.addEventListener('DOMContentLoaded', () => {
  // Function to fetch coffee items data
  const fetchCoffeeItems = async () => {
    try {
      const response = await fetch('./db.json');
      if (!response.ok) {
        throw new Error('Failed to fetch data');
      }
      const data = await response.json();
      return data.coffeeItems || [];
    } catch (error) {
      console.error('Error fetching data:', error);
      return [];
    }
  };

  // Call the function to fetch data
  fetchCoffeeItems().then(coffeeItems => {
    // Now you can use the fetched coffeeItems data
    console.log(coffeeItems);

    // Create a container to hold the menu items
    const menuContainer = document.createElement('div');
    menuContainer.id = 'menu-container';

    // Use the data to create HTML elements dynamically
    coffeeItems.forEach(item => {
      const menuItem = document.createElement('div');
      menuItem.innerHTML = `
        <img src="${item.image}" alt="${item.name}" />
        <h3>${item.name}</h3>
        <p>Price: $${item.price.toFixed(2)}</p>
      `;
      // Append the menuItem to the menu container
      menuContainer.appendChild(menuItem);
    });

    // Append the menu container to the body or another desired element
    document.body.appendChild(menuContainer);
  });

});
};

export default Menu;
