import "./../App.css";
import React from 'react';
import {BrowserRouter as Router, Link, Route} from 'react-router-dom'; 

document.addEventListener("DOMContentLoaded", function () {
  // Fetch coffee items from the JSON file
  fetch('db.json')
    .then(response => response.json())
    .then(data => displayCoffeeItems(data.coffeeItems))
    .catch(error => console.error('Error fetching data:', error));

  // Function to display coffee items
  function displayCoffeeItems(coffeeItems) {
    const coffeeContainer = document.getElementById('coffee-container');

    coffeeItems.forEach(item => {
      const coffeeItemDiv = document.createElement('div');
      coffeeItemDiv.classList.add('coffee-item');

      const img = document.createElement('img');
      img.src = item.image;
      img.alt = item.name;

      const name = document.createElement('h2');
      name.textContent = item.name;

      const price = document.createElement('p');
      price.textContent = `$${item.price.toFixed(2)}`;

      coffeeItemDiv.appendChild(img);
      coffeeItemDiv.appendChild(name);
      coffeeItemDiv.appendChild(price);

      coffeeContainer.appendChild(coffeeItemDiv);
    });
  }
});
export default Menu;
