import React, { useState, useEffect } from "react";
import ReviewBox from "./ReviewBox";
import ReviewForm from "./ReviewForm";

const ReviewPage = () => {
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({
    author: "",
    rating: 0,
    content: "",
  });

  useEffect(() => {
    loadReviews();
  }, []);

  // GET request to fetch all reviews
  const loadReviews = () => {
    fetch("/reviews")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Error fetching reviews: ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => {
        setReviews(data);
      })
      .catch((error) => {
        console.error(error.message);
      });
  };

  // POST request to create a new review
  const addReview = () => {
    fetch("/reviews", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newReview),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Error adding review: ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => {
        // Update the reviews state with the new review
        setReviews([...reviews, data]);

        // Clear the form fields
        setNewReview({
          author: "",
          rating: 0,
          content: "",
        });
      })
      .catch((error) => {
        console.error(error.message);
      });
  };

  // PUT request to update a specific review by ID
  const updateReview = (id, updatedReview) => {
    fetch(`/reviews/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedReview),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Error updating review: ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => {
        // Update the reviews state with the updated review
        setReviews(reviews.map((review) => (review.id === id ? data : review)));
      })
      .catch((error) => {
        console.error(error.message);
      });
  };

  // DELETE request to delete a specific review by ID
  const deleteReview = (id) => {
    fetch(`/reviews/${id}`, {
      method: "DELETE",
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Error deleting review: ${response.statusText}`);
        }
      })
      .then(() => {
        // Update the reviews state by removing the deleted review
        setReviews(reviews.filter((review) => review.id !== id));
      })
      .catch((error) => {
        console.error(error.message);
      });
  };

  return (
    <div>
      <h1>Reviews</h1>
      <ul>
        {reviews.map((review) => (
          <li key={review.id}>
            <ReviewBox
              author={review.author}
              rating={review.rating}
              content={review.content}
            />

            {/* Button to update a review */}
            <button
              onClick={() =>
                updateReview(review.id, { content: "Updated Content" })
              }
            >
              Update
            </button>

            {/* Button to delete a review */}
            <button onClick={() => deleteReview(review.id)}>Delete</button>
          </li>
        ))}
      </ul>
      {/* Form to add a new review */}
      <div>
        <h2>Add a Review</h2>
        <ReviewForm addReview={addReview} />
      </div>
    </div>
  );
};

export default ReviewPage;
