import React from 'react';
import MenuItems from './MenuItems';
import menuData from '.././db.json'
import { useState, useEffect } from "react";
import './../MenuPage.css';

const MenuPage = () => {


  // getting all the menu items
  const [menuItems, setMenuItems] = useState([]);
  useEffect(() => {
    setMenuItems(menuData.coffeeItems);
    document.title = 'Menu - The Little Corner Cafe';
  }, []);


<ToastContainer
position="bottom-center"
autoClose={5000}
hideProgressBar={false}
newestOnTop={false}
closeOnClick
rtl={false}
pauseOnFocusLoss
draggable
pauseOnHover
theme="light"
/>


  // displaying them using our MenuItems component (you can style more)
  return (
    <div className="menu-page">
      <h1>Menu</h1>
      <div className="menu-list">
        {menuItems.map((item) => (
          <MenuItems key={item.id} id={item.id} name={item.name} price={item.price} />
        ))}
      </div>
    </div>
  );
};

export default MenuPage;
