import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import menuData from '.././db.json';
import './../MenuItemImage.css';

const MenuItemImage = () => {
  const { id } = useParams();

  const [menuItem, setMenuItem] = useState([]);
  useEffect(() => {
    setMenuItem(menuData.coffeeItems.map(item => ({ ...item, isBookmarked: false })));
  }, []);

  const focusedItem = menuItem.find((item) => item.id == id);

  const [isBookmarked, setIsBookmarked] = useState(false);

  const handleBookmarkClick = () => {
    const updatedMenuItems = menuItem.map((item) =>
      item.id === focusedItem.id ? { ...item, isBookmarked: !isBookmarked } : item
    );

    // Update the local JSON file (db.json)
    menuData.coffeeItems = updatedMenuItems;

    // Toggle the bookmark state
    setIsBookmarked(!isBookmarked);

    console.log('Bookmark status:', isBookmarked ? 'Removed' : 'Added');
  };

  const handleDeleteClick = () => {
    const updatedMenuItems = menuItem.filter((item) => item.id !== focusedItem.id);

    // Update the local JSON file (db.json)
    menuData.coffeeItems = updatedMenuItems;

    console.log('Item deleted:', focusedItem.name);
    // Handle navigation or UI updates as needed
  };

  if (!menuItem) {
    return <div>Loading...</div>;
  }

  return (
    <div className="menu-item-details">
      {focusedItem && (
        <>
          <h2>{focusedItem.name}</h2>
          <img src={focusedItem.image} alt={focusedItem.name} />
          <button onClick={handleBookmarkClick}>
            {isBookmarked ? 'Remove from Favorites' : 'Add to Favorites'}
          </button>
        </>
      )}
    </div>
  );
};

export default MenuItemImage;
