// MenuItemImage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import menuData from '.././db.json'
import './../MenuItemImage.css'; 

// component only shows name & image
// todo: style image so its not giant and make them all the same

const MenuItemImage = () => {
  // getting the id we got from the hyperlink
  const { id } = useParams();


  // searching our db 
  const [menuItem, setMenuItem] = useState([]);
  useEffect(() => {
    setMenuItem(menuData.coffeeItems);
  }, []);

  // and finding the entry that matches our id
  const focusedItem = menuItem.find((item) => item.id == id);

  useEffect(() => {
    if (focusedItem) {
      document.title = `${focusedItem.name} - The Little Corner Cafe`;
    }
  }, [focusedItem.name]);


  // todo: add bookmark button using this handler
  const [isBookmarked, setIsBookmarked] = useState(false);

  const handleBookmarkClick = () => {
    setIsBookmarked(!isBookmarked);
  };

  if (!focusedItem) {
    return <div>Loading...</div>;
  }

  return (
    <div className="menu-item-details">
      <>
        <h2>{focusedItem.name}</h2>
        <img src={focusedItem.image} alt={focusedItem.name} />
        <button onClick={handleBookmarkClick}>
          {isBookmarked ? 'Remove from Favorites' : 'Add to Favorites'}
        </button>
      </>
    </div>
  );
};

export default MenuItemImage;
