import React, { useState, useEffect } from 'react';
import IndividualLocation from './IndividualLocations';
import menuData from '.././db.json'
import './Locations.css';


const Locations = () => {
  // getting location info from our db
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    setLocations(menuData.locations);
    document.title = 'Locations - The Little Corner Cafe';
  }, []);

  // making it pretty & decluttering
  // style to make it look nice
  return (
    <div className="locations">
      <h1>All Locations</h1>
      {Object.keys(locations).map((city) => (
        <div key={city}>
          <h2>{city}</h2>
          {locations[city].map((location) => (
            <IndividualLocation key={location.name} name={location.name} address={location.address} />
          ))}
        </div>
      ))}
    </div>
  );
};

export default Locations;
