import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import menuData from ".././db.json";
import "./../MenuItemImage.css";

const MenuItemImage = () => {
  const { id } = useParams();
  const [menuItem, setMenuItem] = useState([]);
  const [focusedItem, setFocusedItem] = useState(null);

  useEffect(() => {
    setMenuItem(menuData.coffeeItems);
  }, []);

  useEffect(() => {
    const item = menuItem.find((item) => item.id == id);
    setFocusedItem(item);
    document.title = `${item ? item.name : "Loading"} - The Little Corner Cafe`;
  }, [id, menuItem]);

  const handleBookmarkClick = async () => {
    const updatedMenuData = [...menuItem];
    const index = updatedMenuData.findIndex(
      (item) => item.id === focusedItem.id,
    );

    // update the item
    const updatedItem = { ...focusedItem };
    updatedMenuData[index] = updatedItem;

    setMenuItem(updatedMenuData);

    //  update the db.json file
    await fetch(`/api/coffeeItems/${focusedItem.id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedItem),
    });

    setIsBookmarked(!isBookmarked);
  };

  const handleDeleteClick = async () => {
    // request to remove the item
    const updatedMenuData = menuItem.filter(
      (item) => item.id !== focusedItem.id,
    );
    setMenuItem(updatedMenuData);

    // DELETE request to update the db.json file
    await fetch(`/api/coffeeItems/${focusedItem.id}`, {
      method: "DELETE",
    });
  };

  if (!focusedItem) {
    return <div>Loading...</div>;
  }

  return (
    <div className="menu-item-details">
      <>
        <h2>{focusedItem.name}</h2>
        <img src={focusedItem.image} alt={focusedItem.name} />
        <p></p>
      </>
    </div>
  );
};

export default MenuItemImage;
