import React, { useState, useEffect } from "react";
import axios from "axios";
import ReviewBox from "./ReviewBox";
import ReviewForm from "./ReviewForm";

const ReviewPage = () => {
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({
    author: "",
    rating: 0,
    content: "",
  });

  useEffect(() => {
    loadReviews();
  }, []);

  // GET request to fetch all reviews
  const loadReviews = () => {
    axios
      .get("http://localhost:3000/reviews")
      .then((response) => {
        setReviews(response.data);
      })
      .catch((error) => {
        console.error(`Error fetching reviews: ${error.message}`);
      });
  };

  // POST request to create a new review
  const addReview = () => {
    axios
      .post("/reviews", newReview, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((response) => {
        // Update the reviews state with the new review
        setReviews([...reviews, response.data]);

        // Clear the form fields
        setNewReview({
          author: "",
          rating: 0,
          content: "",
        });
      })
      .catch((error) => {
        console.error(`Error adding review: ${error.message}`);
      });
  };

  // PUT request to update a specific review by ID
  const updateReview = (id, updatedReview) => {
    axios
      .put(`/reviews/${id}`, updatedReview, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((response) => {
        // Update the reviews state with the updated review
        setReviews(
          reviews.map((review) => (review.id === id ? response.data : review)),
        );
      })
      .catch((error) => {
        console.error(`Error updating review: ${error.message}`);
      });
  };

  // DELETE request to delete a specific review by ID
  const deleteReview = (id) => {
    axios
      .delete(`/reviews/${id}`)
      .then(() => {
        // Update the reviews state by removing the deleted review
        setReviews(reviews.filter((review) => review.id !== id));
      })
      .catch((error) => {
        console.error(`Error deleting review: ${error.message}`);
      });
  };

  return (
    <div>
      <h1>Reviews</h1>
      <ul>
        {reviews.map((review) => (
          <li key={review.id}>
            <ReviewBox
              author={review.author}
              rating={review.rating}
              content={review.content}
            />

            {/* Button to update a review */}
            <button
              onClick={() =>
                updateReview(review.id, { content: "Updated Content" })
              }
            >
              Update
            </button>

            {/* Button to delete a review */}
            <button onClick={() => deleteReview(review.id)}>Delete</button>
          </li>
        ))}
      </ul>
      {/* Form to add a new review */}
      <div>
        <h2>Add a Review</h2>
        <ReviewForm addReview={addReview} />
      </div>
    </div>
  );
};

export default ReviewPage;
