import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import menuData from '.././db.json';



const Favorites = ({ menuItems }) => {
  const [favoriteItems, setFavoriteItems] = useState([]);

  useEffect(() => {
    // Filter out the items with isBookmarked property set to true
    const favorites = menuItems.filter((item) => item.isBookmarked);

    setFavoriteItems(favorites);
  }, [menuItems]);

  return (
    <div>
      <h2>Favorites</h2>
      {favoriteItems.length === 0 ? (
        <p>No favorites yet. Bookmark some items from the menu!</p>
      ) : (
        <ul>
          {favoriteItems.map((item) => (
            <li key={item.id}>
              <Link to={`/menu/${item.id}`}>
                {item.name} - ${item.price}
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Favorites;
