import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import menuData from './../db.json';

const Favorites = () => {
  const { userId } = useParams();
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    // Fetch or set your menu items data
    // For now, let's assume menuData.coffeeItems as your initial data
    setMenuItems(menuData.coffeeItems);
  }, []);

  const favoriteItems = menuItems.filter((item) => item.isBookmarked);

  return (
    <div>
      <h2>Favorites for User {userId}</h2>
      {favoriteItems.length === 0 ? (
        <p>No favorites yet. Bookmark some items from the menu!</p>
      ) : (
        <ul>
          {favoriteItems.map((item) => (
            <li key={item.id}>
              <Link to={`/menu/${item.id}`}>
                {item.name} - ${item.price}
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Favorites;
