// MenuItemImage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import menuData from '.././db.json'

const MenuItemImage = () => {
  const { id } = useParams();

  const [menuItem, setMenuItem] = useState(null);

  useEffect(() => {
    // Use the imported data directly in development
    setMenuItem(menuData.coffeeItems);
  }, []);

  const focusedItem = menuItem.find((item) => item.id === id);


  if (!menuItem) {
    return <div>Loading...</div>;
  }

  const { name, image } = menuItem;
  console.log('mica msg image', image);

  return (
    <div className="menu-item-details">
      {item && (
        <>
          <h2>{focusedItem.name}</h2>
          <img src={focusedItem.image} alt={focusedItem.name} />
        </>
      )}
    </div>
  );
};

export default MenuItemImage;
