import React, { useState, useEffect } from 'react';
import './../Subscriptions.css';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// form to sign up for newsletter
// todo: add styling and text to page so user knows what they are signing up for
const Subscriptions = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
  });

  const [errors, setErrors] = useState({
    name: '',
    phone: '',
    email: '',
  });


  const handleSubmit = (e) => {
    e.preventDefault();


    if (validateForm()) {
      // Form is valid, user can submit the data
      console.log('Form submitted:', formData);
      // Show success toast
      toast.success('Subscription successful!', {
        position: 'bottom-center',
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: 'light',
      });
    } else {
      // Form is invalid, show error toast
      toast.error('Please fix the errors in the form.', {
        position: 'bottom-center',
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: 'light',
      });
    }
  };

  useEffect(() => {
    document.title = 'Subscriptions - The Little Corner Cafe'; 
    return () => {
    };
  }, []);

  return (
    <div className="subscriptions-page">
      <h1>Coffee News Subscription</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} />
          <span className="error">{errors.name}</span>
        </div>
        <div>
          <label htmlFor="phone">Phone:</label>
          <input type="tel" id="phone" name="phone" value={formData.phone} onChange={handleChange} />
          <span className="error">{errors.phone}</span>
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} />
          <span className="error">{errors.email}</span>
        </div>
        <button type="submit">Subscribe</button>
      </form>
      <ToastContainer
        position="bottom-center"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
    </div>
  );
};

export default Subscriptions;
