import React, { useState, useEffect } from "react";
import axios from "axios";
import ReviewBox from "./ReviewBox";

const ReviewPage = () => {
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({
    author: "",
    rating: 0,
    content: "",
  });

  useEffect(() => {
    // Load reviews when the component mounts
    loadReviews();
  }, []);

  //   const loadReviews = () => {
  //     // GET request to fetch all reviews
  //     axios
  //       .get("/api/reviews")
  //       .then((response) => {
  //         setReviews(response.data);
  //       })
  //       .catch((error) => {
  //         console.error("Error fetching reviews:", error);
  //       });
  //   };

  const addReview = () => {
    // POST request to create a new review
    axios
      .post("/api/reviews", newReview)
      .then((response) => {
        // Update the reviews state with the new review
        setReviews([...reviews, response.data]);

        // Clear the form fields
        setNewReview({
          // Reset form fields
        });
      })
      .catch((error) => {
        console.error("Error adding review:", error);
      });
  };

  const updateReview = (id, updatedReview) => {
    // PUT request to update a specific review by ID
    axios
      .put(`/api/reviews/${id}`, updatedReview)
      .then((response) => {
        // Update the reviews state with the updated review
        setReviews(
          reviews.map((review) => (review.id === id ? response.data : review)),
        );
      })
      .catch((error) => {
        console.error("Error updating review:", error);
      });
  };

  const deleteReview = (id) => {
    // DELETE request to delete a specific review by ID
    axios
      .delete(`/api/reviews/${id}`)
      .then(() => {
        // Update the reviews state by removing the deleted review
        setReviews(reviews.filter((review) => review.id !== id));
      })
      .catch((error) => {
        console.error("Error deleting review:", error);
      });
  };

  return (
    <div>
      <h1>Reviews</h1>
      <ul>
        {reviews.map((review) => (
          <li key={review.id}>
            {/* Use the ReviewBox component to display each review */}
            <ReviewBox
              author={review.author}
              rating={review.rating}
              content={review.content}
            />

            {/* Button to update a review */}
            <button
              onClick={() =>
                updateReview(review.id, { content: "Updated Content" })
              }
            >
              Update
            </button>

            {/* Button to delete a review */}
            <button onClick={() => deleteReview(review.id)}>Delete</button>
          </li>
        ))}
      </ul>
      {/* Form to add a new review */}
      <div>
        <h2>Add a Review</h2>
        {/* Form fields for creating a new review */}
        <button onClick={addReview}>Submit Review</button>
      </div>
    </div>
  );
};

export default ReviewPage;
