// MenuItemImage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const MenuItemImage = (match) => {
  const { id } = match.params;

  const [menuItem, setMenuItem] = useState(null);

  useEffect(() => {
    const fetchMenuItemById = async (id) => {
      try {
        const response = await fetch(`/menu/${id}`);
        if (!response.ok) {
          throw new Error(`Failed to fetch menu item with ID ${id}`);
        }
        const data = await response.json();
        setMenuItem(data);
      } catch (error) {
        console.error('Error fetching menu item:', error);
        // Handle error, e.g., setMenuItem(null) or display an error message
      }
    };

    fetchMenuItemById(id);
  }, [id]);

  if (!menuItem) {
    return <div>Loading...</div>;
  }

  const { name, image } = menuItem;
  console.log('mica msg image', image);

  return (
    <div className="menu-item-details">
      {menuItem && (
        <>
          <h2>{menuItem.name}</h2>
          <img src={menuItem.image} alt={menuItem.name} />
          {/* Display other details if needed */}
        </>
      )}
    </div>
  );
};

export default MenuItemImage;
