// MenuItemImage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { fetchMenuItemById } from '.././api';

const MenuItemImage = () => {
  const { id } = useParams();

  const [menuItem, setMenuItem] = useState(null);


  useEffect(() => {
    const fetchMenuItemById = async (id) => {
      try {
        const response = await fetch(`${API_BASE_URL}/menu/${id}`);
        if (!response.ok) {
          throw new Error(`Failed to fetch menu item with ID ${id}`);
        }
        const data = await response.json();
        setMenuItem(data);
      } catch (error) {
        console.error('Error fetching menu item:', error);
        // Handle error, e.g., setMenuItem(null) or display an error message
      }
    };

    fetchMenuItemById(itemId);
  }, [itemId]);
  if (!menuItem) {
    return <div>Loading...</div>;
  }

  const { name, image } = menuItem;
  console.log('mica msg image', image);

  return (
    <div className="menu-item-image">
      <h2>{name}</h2>
      <img src={image} alt={name} />
    </div>
  );
};

export default MenuItemImage;
