// MenuItemImage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const MenuItemImage = () => {
  const { id } = useParams();
  const [menuItem, setMenuItem] = useState(null);

  useEffect(() => {
    // Fetch menu item details based on itemId (replace with your actual data fetching logic)
    // This assumes you have a function to fetch a menu item by its ID
    const fetchMenuItem = async () => {
      try {
        const response = await fetch(`/menuItem/${id}`); // Replace with your API endpoint
        const data = await response.json();
        setMenuItem(data);
      } catch (error) {
        console.error('Error fetching menu item:', error);
      }
    };

    fetchMenuItem();
  }, [id]);

  if (!menuItem) {
    return <div>Loading...</div>;
  }

  const { name, image } = menuItem;

  return (
    <div className="menu-item-image">
      <h2>{name}</h2>
      <img src={image} alt={name} />
    </div>
  );
};

export default MenuItemImage;
