import React, { useState, useEffect } from 'react';

// form to sign up for newsletter
// todo: add styling and text to page so user knows what they are signing up for
const Subscriptions = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
  });

  const [errors, setErrors] = useState({
    name: '',
    phone: '',
    email: '',
  });

  const validateForm = () => {
    let isValid = true;
    const newErrors = { name: '', phone: '', email: '', store: '' };

    // Custom validation logic
    if (formData.name.trim() === '') {
      newErrors.name = 'Name is required';
      isValid = false;
    }

    if (!/^\d{10}$/.test(formData.phone)) {
      newErrors.phone = 'Please enter a valid 10-digit phone number';
      isValid = false;
    }

    if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      // Form is valid, you can submit the data or perform further actions
      console.log('Form submitted:', formData);
    } else {
      // Form is invalid, you can handle errors or display messages
      console.log('Form validation failed');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div className="subscriptions-page">
      <h1>Coffee News Subscription</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} />
          <span className="error">{errors.name}</span>
        </div>
        <div>
          <label htmlFor="phone">Phone:</label>
          <input type="tel" id="phone" name="phone" value={formData.phone} onChange={handleChange} />
          <span className="error">{errors.phone}</span>
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} />
          <span className="error">{errors.email}</span>
        </div>
        <button type="submit">Subscribe</button>
      </form>
    </div>
  );
};

export default Subscriptions;
